<script>import { melt } from "@melt-ui/svelte";
import { getCtx, getAttrs } from "../ctx.js";
export let asChild = false;
const {
  elements: { portalled }
} = getCtx();
const attrs = getAttrs("portal");
$:
  builder = $portalled;
$:
  Object.assign(builder, attrs);
</script>

{#if asChild}
	<slot {builder} />
{:else}
	<div {...builder} use:builder.action {...$$restProps}>
		<slot {builder} />
	</div>
{/if}
