<script>import { melt } from "@melt-ui/svelte";
import { getAttrs, getCtx } from "../ctx.js";
export let asChild = false;
const {
  elements: { grid }
} = getCtx();
const attrs = getAttrs("grid");
$:
  builder = $grid;
$:
  Object.assign(builder, attrs);
</script>

{#if asChild}
	<slot {builder} />
{:else}
	<table {...builder} use:builder.action {...$$restProps}>
		<slot {builder} />
	</table>
{/if}
