<script>import { getAttrs, getRadioIndicator } from "../ctx.js";
export let asChild = false;
const { isChecked, value } = getRadioIndicator();
const attrs = getAttrs("radio-indicator");
$:
  checked = $isChecked(value);
</script>

{#if asChild}
	<slot {attrs} {checked} />
{:else}
	<div {...$$restProps} {...attrs}>
		{#if checked}
			<slot {attrs} {checked} />
		{/if}
	</div>
{/if}
