<script>import { melt } from "@melt-ui/svelte";
import { getCtx, getAttrs } from "../ctx.js";
export let asChild = false;
const {
  elements: { separator }
} = getCtx();
const attrs = getAttrs("separator");
$:
  builder = $separator;
$:
  Object.assign(builder, attrs);
</script>

{#if asChild}
	<slot {builder} />
{:else}
	<div {...$separator} use:$separator.action {...$$restProps} />
{/if}
