<script>import { createDispatcher } from "../../../internal/events.js";
import { melt } from "@melt-ui/svelte";
import { getCtx, getAttrs, updatePositioning } from "../ctx.js";
export let transition = void 0;
export let transitionConfig = void 0;
export let inTransition = void 0;
export let inTransitionConfig = void 0;
export let outTransition = void 0;
export let outTransitionConfig = void 0;
export let asChild = false;
export let id = void 0;
export let side = "bottom";
export let align = "center";
export let sideOffset = 0;
export let alignOffset = 0;
export let collisionPadding = 8;
export let avoidCollisions = true;
export let collisionBoundary = void 0;
export let sameWidth = true;
export let fitViewport = false;
const {
  elements: { menu },
  states: { open },
  ids
} = getCtx();
const dispatch = createDispatcher();
const attrs = getAttrs("content");
$:
  if (id) {
    ids.menu.set(id);
  }
$:
  builder = $menu;
$:
  Object.assign(builder, attrs);
$:
  updatePositioning({
    side,
    align,
    sideOffset,
    alignOffset,
    collisionPadding,
    avoidCollisions,
    collisionBoundary,
    sameWidth,
    fitViewport
  });
</script>

<!-- svelte-ignore a11y-no-static-element-interactions / applied by melt's builder-->
{#if asChild && $open}
	<slot {builder} />
{:else if transition && $open}
	<div
		transition:transition={transitionConfig}
		{...builder} use:builder.action
		{...$$restProps}
		on:m-pointerleave={dispatch}
		on:keydown
	>
		<slot {builder} />
	</div>
{:else if inTransition && outTransition && $open}
	<div
		in:inTransition={inTransitionConfig}
		out:outTransition={outTransitionConfig}
		{...builder} use:builder.action
		{...$$restProps}
		on:m-pointerleave={dispatch}
		on:keydown
	>
		<slot {builder} />
	</div>
{:else if inTransition && $open}
	<div
		in:inTransition={inTransitionConfig}
		{...builder} use:builder.action
		{...$$restProps}
		on:m-pointerleave={dispatch}
		on:keydown
	>
		<slot {builder} />
	</div>
{:else if outTransition && $open}
	<div
		out:outTransition={outTransitionConfig}
		{...builder} use:builder.action
		{...$$restProps}
		on:m-pointerleave={dispatch}
		on:keydown
	>
		<slot {builder} />
	</div>
{:else if $open}
	<div
		{...builder} use:builder.action
		{...$$restProps}
		on:m-pointerleave={dispatch}
		on:keydown
	>
		<slot {builder} />
	</div>
{/if}
