<script>import { getAttrs, getItemIndicator } from "../ctx.js";
export let asChild = false;
const { isSelected, value } = getItemIndicator();
const attrs = getAttrs("indicator");
</script>

{#if asChild}
	<slot {attrs} isSelected={$isSelected(value)} />
{:else}
	<div {...$$restProps} {...attrs}>
		{#if $isSelected(value)}
			<slot {attrs} isSelected={$isSelected(value)} />
		{/if}
	</div>
{/if}
