<script>import { melt } from "@melt-ui/svelte";
import { getCtx, getAttrs } from "../ctx.js";
export let asChild = false;
const {
  elements: { range }
} = getCtx();
const attrs = getAttrs("range");
$:
  builder = $range;
$:
  Object.assign(builder, attrs);
</script>

{#if asChild}
	<slot {builder} />
{:else}
	<span {...builder} use:builder.action {...$$restProps} />
{/if}
