<script>import { melt } from "@melt-ui/svelte";
import { setArrow, getAttrs } from "../ctx.js";
export let size = 8;
export let asChild = false;
const {
  elements: { arrow }
} = setArrow(size);
const attrs = getAttrs("arrow");
$:
  builder = $arrow;
$:
  Object.assign(builder, attrs);
</script>

{#if asChild}
	<slot {builder} />
{:else}
	<div {...builder} use:builder.action {...$$restProps} />
{/if}
