<script>import { Dialog as DialogPrimitive } from "bits-ui";
import { Command } from "../..";
export let open = false;
export let value = void 0;
export let portal = void 0;
export let overlayClasses = void 0;
export let contentClasses = void 0;
export let contentTransition = void 0;
export let contentTransitionConfig = void 0;
export let contentInTransition = void 0;
export let contentInTransitionConfig = void 0;
export let contentOutTransition = void 0;
export let contentOutTransitionConfig = void 0;
export let overlayTransition = void 0;
export let overlayTransitionConfig = void 0;
export let overlayInTransition = void 0;
export let overlayInTransitionConfig = void 0;
export let overlayOutTransition = void 0;
export let overlayOutTransitionConfig = void 0;
$:
  overlayProps = {
    class: overlayClasses,
    transition: overlayTransition,
    transitionConfig: overlayTransitionConfig,
    inTransition: overlayInTransition,
    inTransitionConfig: overlayInTransitionConfig,
    outTransition: overlayOutTransition,
    outTransitionConfig: overlayOutTransitionConfig,
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    "data-cmdk-overlay": ""
  };
$:
  contentProps = {
    class: contentClasses,
    transition: contentTransition,
    transitionConfig: contentTransitionConfig,
    inTransition: contentInTransition,
    inTransitionConfig: contentInTransitionConfig,
    outTransition: contentOutTransition,
    outTransitionConfig: contentOutTransitionConfig,
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    "data-cmdk-dialog": ""
  };
export let label = void 0;
</script>

<DialogPrimitive.Root bind:open {...$$restProps}>
	{#if portal === null}
		<DialogPrimitive.Overlay {...overlayProps} />
		<DialogPrimitive.Content aria-label={label} {...contentProps}>
			<Command.Root {...$$restProps} bind:value>
				<slot />
			</Command.Root>
		</DialogPrimitive.Content>
	{:else}
		<DialogPrimitive.Portal>
			<DialogPrimitive.Overlay {...overlayProps} />
			<DialogPrimitive.Content aria-label={label} {...contentProps}>
				<Command.Root {...$$restProps} bind:value>
					<slot />
				</Command.Root>
			</DialogPrimitive.Content>
		</DialogPrimitive.Portal>
	{/if}
</DialogPrimitive.Root>
