<script>export let progress = 0;
export let asChild = false;
$:
  attrs = {
    "data-cmdk-loading": "",
    role: "progressbar",
    "aria-valuenow": progress,
    "aria-valuemin": 0,
    "aria-valuemax": 100,
    "aria-label": "Loading..."
  };
</script>

{#if asChild}
	<slot {attrs} />
{:else}
	<div {...attrs} {...$$restProps}>
		<div aria-hidden>
			<slot {attrs} />
		</div>
	</div>
{/if}
