<script>import { melt } from "@melt-ui/svelte";
import { getContent, getAttrs } from "../ctx.js";
export let transition = void 0;
export let transitionConfig = void 0;
export let inTransition = void 0;
export let inTransitionConfig = void 0;
export let outTransition = void 0;
export let outTransitionConfig = void 0;
export let asChild = false;
const { content, isSelected, props } = getContent();
const attrs = getAttrs("content");
$:
  builder = $content(props);
</script>

{#if asChild && $isSelected(props)}
	<slot {builder} {attrs} />
{:else if transition && $isSelected(props)}
	<div transition:transition={transitionConfig} {...builder} use:builder.action {...$$restProps} {...attrs}>
		<slot {builder} {attrs} />
	</div>
{:else if inTransition && outTransition && $isSelected(props)}
	<div
		in:inTransition={inTransitionConfig}
		out:outTransition={outTransitionConfig}
		{...builder} use:builder.action
		{...$$restProps}
		{...attrs}
	>
		<slot {builder} {attrs} />
	</div>
{:else if inTransition && $isSelected(props)}
	<div in:inTransition={inTransitionConfig} {...builder} use:builder.action {...$$restProps} {...attrs}>
		<slot {builder} {attrs} />
	</div>
{:else if outTransition && $isSelected(props)}
	<div out:outTransition={outTransitionConfig} {...builder} use:builder.action {...$$restProps} {...attrs}>
		<slot {builder} {attrs} />
	</div>
{:else if $isSelected(props)}
	<div {...builder} use:builder.action {...$$restProps} {...attrs}>
		<slot {builder} {attrs} />
	</div>
{/if}
