<script>import { melt } from "@melt-ui/svelte";
import { getCtx, getAttrs } from "../ctx.js";
export let asChild = false;
const {
  elements: { fallback }
} = getCtx();
$:
  builder = $fallback;
const attrs = getAttrs("fallback");
</script>

{#if asChild}
	<slot {builder} {attrs} />
{:else}
	<span {...builder} use:builder.action {...$$restProps} {...attrs}>
		<slot {builder} {attrs} />
	</span>
{/if}
