<script>import { melt } from "@melt-ui/svelte";
import { getCtx, getAttrs } from "../ctx.js";
export let asChild = false;
const {
  elements: { separator }
} = getCtx();
$:
  builder = $separator;
const attrs = getAttrs("separator");
</script>

{#if asChild}
	<slot {builder} {attrs} />
{:else}
	<div {...$separator} use:$separator.action {...$$restProps} {...attrs} />
{/if}
