<script>import { createDispatcher } from "../../../internal/index.js";
import { melt } from "@melt-ui/svelte";
import { getContent, getAttrs } from "../ctx.js";
export let sideOffset = 5;
export let transition = void 0;
export let transitionConfig = void 0;
export let inTransition = void 0;
export let inTransitionConfig = void 0;
export let outTransition = void 0;
export let outTransitionConfig = void 0;
export let asChild = false;
export let id = void 0;
const {
  elements: { menu },
  states: { open },
  ids
} = getContent(sideOffset);
const dispatch = createDispatcher();
$:
  if (id) {
    ids.menu.set(id);
  }
$:
  builder = $menu;
const attrs = getAttrs("content");
</script>

{#if asChild && $open}
	<slot {builder} {attrs} />
{:else if transition && $open}
	<div
		transition:transition={transitionConfig}
		{...builder} use:builder.action
		{...$$restProps}
		{...attrs}
		on:m-keydown={dispatch}
	>
		<slot {builder} {attrs} />
	</div>
{:else if inTransition && outTransition && $open}
	<div
		in:inTransition={inTransitionConfig}
		out:outTransition={outTransitionConfig}
		{...builder} use:builder.action
		{...$$restProps}
		{...attrs}
		on:m-keydown={dispatch}
	>
		<slot {builder} {attrs} />
	</div>
{:else if inTransition && $open}
	<div
		in:inTransition={inTransitionConfig}
		{...builder} use:builder.action
		{...$$restProps}
		{...attrs}
		on:m-keydown={dispatch}
	>
		<slot {builder} {attrs} />
	</div>
{:else if outTransition && $open}
	<div
		out:outTransition={outTransitionConfig}
		{...builder} use:builder.action
		{...$$restProps}
		{...attrs}
		on:m-keydown={dispatch}
	>
		<slot {builder} {attrs} />
	</div>
{:else if $open}
	<div {...builder} use:builder.action {...$$restProps} {...attrs} on:m-keydown={dispatch}>
		<slot {builder} {attrs} />
	</div>
{/if}
