<script>import { melt } from "@melt-ui/svelte";
import { setGroupCtx, getAttrs } from "../ctx.js";
export let asChild = false;
const { group, id } = setGroupCtx();
$:
  builder = $group(id);
const attrs = getAttrs("group");
</script>

{#if asChild}
	<slot {builder} {attrs} />
{:else}
	<div {...builder} use:builder.action {...$$restProps} {...attrs}>
		<slot {builder} {attrs} />
	</div>
{/if}
