<script>import { melt } from "@melt-ui/svelte";
import { getCtx, getAttrs } from "../ctx.js";
export let asChild = false;
const {
  elements: { hiddenInput },
  options: { disabled }
} = getCtx();
$:
  builder = $hiddenInput;
const attrs = getAttrs("input");
</script>

{#if asChild}
	<slot {builder} {attrs} />
{:else}
	<input {...builder} use:builder.action {...$$restProps} {...attrs} disabled={$disabled ? true : undefined} />
{/if}
