<script>import { getCtx, getAttrs } from "../ctx.js";
const {
  states: { value }
} = getCtx();
function getValue(value2) {
  if (value2.length === 1) {
    return value2[0];
  } else {
    return value2[1] - value2[0];
  }
}
$:
  inputValue = getValue($value);
const attrs = getAttrs("input");
</script>

<input {...$$restProps} value={inputValue} hidden {...attrs} />
