<script>import { melt } from "@melt-ui/svelte";
import { getCtx, getAttrs } from "../ctx.js";
export let value;
export let asChild = false;
const {
  elements: { content }
} = getCtx();
$:
  builder = $content(value);
const attrs = getAttrs("content");
</script>

{#if asChild}
	<slot {builder} {attrs} />
{:else}
	<div {...builder} use:builder.action {...$$restProps} {...attrs}>
		<slot {builder} {attrs} />
	</div>
{/if}
